mapper.insert:
insert into edu_base_id_pivot (id, native_id, id_type, 
  external_org, external_id, remark, 
  status, create_time, update_time
  )
values (#{id,jdbcType=BIGINT}, #{nativeId,jdbcType=BIGINT}, #{idType,jdbcType=TINYINT}, 
  #{externalOrg,jdbcType=TINYINT}, #{externalId,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, 
  #{status,jdbcType=TINYINT}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT external_id cardNo,stu.stu_name stuName,stu.stu_nickname stuNickname FROM edu_base_id_pivot bip
INNER JOIN base_student stu on native_id = stu.id
WHERE bip.status = 0 and native_id = #{stuId}
